<template>
    <div class="box is-parent is-centered is-fluid" :class="{'is-waiting': loader}">
        <form @submit.prevent="validateBeforeSubmit">
            <article class="tile is-child is-3">
                <p class="subtitle">Login</p>
                <article class="message is-danger">
                    <div class="message-header" v-if="infoError">
                        <p>Login failed. Please try again.</p>
                    </div>
                </article>
                <div class="field">
                    <p :class="{ 'control': true }" class="control has-icons-left has-icons-right">
                        <input
                                v-model="email"
                                v-validate="'required|email'"
                                :class="{'input': true, 'is-danger': errors.has('email') }"
                                data-vv-delay="1000"
                                name="email"
                                type="text"
                                placeholder="Email">
                        <span v-show="errors.has('email')" class="help is-danger">{{ errors.first('email') }}</span>
                        <span class="icon is-small is-left is-success">
                            <i class="fa fa-envelope"></i>
                        </span>
                        <span class="icon is-small is-right">
                            <i class="fa" :class="{'icon': true, 'fa-warning': errors.has('email') }"></i>
                        </span>
                    </p>
                </div>
                <div class="field">
                    <p class="control has-icons-left">
                        <input v-validate="'required'" v-model="password" class="input" type="password" placeholder="Password">
                        <span class="icon is-small is-left">
                      <i class="fa fa-lock"></i>
                    </span>
                    </p>
                </div>
                <div class="field">
                    <p class="control">
                        <label class="custom-control custom-checkbox">
                            <input type="checkbox" class="custom-control-input" value="1" v-model="remember" name="remember">
                            <span class="custom-control-indicator"></span>
                            <span class="custom-control-description">Remember Me</span>
                        </label>
                    </p>
                </div>
                <div class="field">
                    <p class="control">
                        <button :disabled="errors.any()" type="submit" class="button is-success">
                            Login
                        </button>
                    </p>
                </div>
                <div class="field">
                    <p class="control">
                        <router-link :to="{ name: 'auth.register' }">Register</router-link>
                    </p>
                </div>
                <div class="field">
                    <p class="control">
                        <router-link to="/password-reset">Lost your password?</router-link>
                    </p>
                </div>
                <div class="field">
                    <p class="control">
                        <a href="#">Facebook</a> | <a href="#">Google</a>
                    </p>
                </div>
            </article>
        </form>
    </div>
</template>

<script>
  import { Validator } from 'vee-validate';
  import axios from 'axios'

export default {
  name: 'login',

  metaInfo: { titleTemplate: 'Login | %s' },

  data () {
    return {
      loader: false,
      infoError: false,
      email: 'tanvir.alam.shawn@gmail.com',
      password: '123123',
      remember: true
    }
  },

  methods: {
    login(){
      this.loader = true
      this.infoError = false

      axios.post('/api/login',{
        email: this.email,
        password: this.password
      })
        .then(response => {
          this.$store.dispatch('saveToken', {
            token: response.data.token,
            remember: this.remember
          })
            this.$store.dispatch('fetchUser').then(() => {
              this.$router.push({
                name: 'Dashboard',
                params: { email: this.email }
              });
            })
        }).catch(error => {
        this.infoError = true
        this.loader = false
        this.password = ''
      })

      /*this.$store.dispatch('login', {
        email: this.email,
        password: this.password
      }).then((response) => {
        console.log(response)
        this.$store.dispatch('saveToken', {
          token: response.data.token,
          remember: this.remember
        })
        this.$store.dispatch('fetchUser').then(() => {
          this.$router.push({
            name: 'Dashboard',
            params: { email: this.email }
          });
        })
      }, () => {
        this.infoError = true
        this.loader = false
        this.password = ''
      })*/

    },

    validateBeforeSubmit() {
      this.$validator.validateAll().then(() => this.login())
    },
  }
}
</script>

<style lang="scss" type="text/scss">
    .is-waiting {
        position: relative;
        transition-duration: .3s;
        > * {
            opacity: .25;
        }
        &:before {
            content: '';
            height: 100%;
            left: 0;
            position: absolute;
            top: 0;
            width: 100%;
            z-index: 9;
        }
        &:after {
            background: {
                image: url(data:image/svg+xml;base64,);
                position: center;
                size: cover;
            }
            content: '';
            height: 64px;
            left: 50%;
            position: absolute;
            top: 50%;
            transform: translate(-50%, -50%);
            width: 64px;
        }
    }
</style>
